import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    isModalOpen: false,
}

const modalSlice = createSlice({
    name: 'modal',
    initialState,
    reducers: {
        openModal: (state, action) => {
            state.isOpen = true;
        },

        closeModal: (state, action) => {
            state.isOpen = false;
        }
    }
})

export const {closeModal, openModal} = modalSlice.actions;
export default modalSlice.reducer;
